import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createUser } from '../../services/register/createUser';
import { useProductContext } from '../../context/ProductContext';
import { Loader } from '../../components/loader/Loader';
import './address.css';

export const Address = () => {
  const [address, setAddress] = useState({});

  const inputHandler = (e) => {
    address[e.target.id] = e.target.value;
    setAddress(address);
    console.log(address);
  };
  return (
    <>
      <div className="address-box">
        <label for="name">
          Name
          <input
            type="text"
            placeholder="Name"
            id="name"
            onChange={inputHandler}
          />
        </label>
        <label for="number">
          Mobile Number
          <input
            type="number"
            placeholder="10-digit mobile number"
            id="number"
            onChange={inputHandler}
          />
        </label>
        <label for="pin">
          PIN code
          <input
            type="number"
            placeholder="6 digit"
            id="pin"
            onChange={inputHandler}
          />
        </label>
        <label for="flat">
          Flat/House no
          <input type="text" id="flat" onChange={inputHandler} />
        </label>
        <label for="area">
          Area/Colony
          <input type="text" id="area" onChange={inputHandler} />
        </label>
      </div>
    </>
  );
};
